require('dotenv').config();
const express = require('express');
const app = express();
const port = process.env.PORT || 5000;
const pool = require('./db');
const helmet = require('helmet'); // helmet

app.use(express.json());
app.use(helmet()); // helmet 

// Routes
app.get('/api/potatoes', async (req, res) => {
    try {
        const result = await pool.query('SELECT * FROM potato_types');
        res.json(result.rows);
    } catch (error) {
        console.error("Error fetching potatoes:", error);
        res.status(500).json({ error: "Failed to fetch potatoes" });
    }
});


app.get('/api/potatoes/:id', async (req, res) => {
    try {
        const id = parseInt(req.params.id); 

        if (isNaN(id)) {
            return res.status(400).json({ error: 'Invalid ID' }); 
        }

        const result = await pool.query('SELECT * FROM potato_types WHERE id = $1', [id]);
        if (result.rows.length > 0) {
            res.json(result.rows[0]);
        } else {
            res.status(404).json({ error: 'Potato not found' });
        }
    } catch (error) {
        console.error("Error fetching potato:", error);
        res.status(500).json({ error: "Failed to fetch potato" });
    }
});

app.listen(port, () => {
    console.log(`Server is listening on port ${port}`);
});